import React from "react";
import Titles from "./components/Titles";
import Form from "./components/Form";
import Weather from "./components/Weather";

const API_KEY = "369042b36225ba7dafd6191848183624";


class App extends React.Component {

    getWeather = async (e) =>{
        e.preventDefault();
        const city = e.target.element.city.value;
        const country = e.target.element.city.value;
        const api_call = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}&units=imperial`);
        const data = await api_call.json();
        console.log(data);

    }


    render(){
        return(
            <div>
                <Titles />
                <Form getWeather={this.getWeather}/>
                <Weather />
            </div>
        );
    }

};


export default App;
